import React from 'react'
import Axios from 'axios'

class App extends React.Component {
    state = {
        DataResult: null,
        SearchData: '',
        PagePosition: 1,
        Config: {
            url: 'http://www.omdbapi.com/?apikey=58ed57ce'
        }
    }

    constructor() {
        this.setState({
            ...this.state,
            SearchData: 'avatar',
        })
    }

    async componentDidMount() {
        this.setState({
            ...this.state,
            Config: {
                url: this.state.url + '&s='+ this.state.SearchData +'&page=' + this.state.PagePosition
            }
        })

        Axios(this.state.Config).then((response) => {
            this.setState({ DataResult: response.data })
        }).catch((err) => { console.log(err) })
    }

    componentDidUpdate = () => {

    }

    OnEnter = (e) => {
        if ( e.key === 'Enter' ) {
            console.log(e)
        }
    }

    render() {
        return (
            <section id="omdb-api">
                <div className="main-content">
                    <div className="box-content">
                        <div className="search-content">
                            <input onKeyDown={ this.OnEnter } id="search-input" name="search-input" placeholder="Searh your movie here.." />
                        </div>
                        <div className="content">

                        </div>
                    </div>
                </div>
            </section>
        )
    }
}

export default App
